import { react, Component } from 'react';
import { Link } from 'react-router-dom';

class SearchPage extends Component {
    constructor() {
        super();
        this.state = {
            productsToRender: []
        }
    }
    render() {
        if(this.state.productsToRender.length === 0) {
            return (<div>
                <input type="text"><button>Botão de pesquisa</button></input>
                <Link to="./ShoppingCart.jsx"><button>Carrinho de compras</button></Link>
                <ul data-testid="home-initial-message">Digite algum termo de pesquisa ou escolha uma categoria</ul>
                </div>)
        }
      return (
        <div>Aqui será adicionado o layout da página após ser feita alguma pesquisa</div>
      );
    }
  }

  export default SearchPage;
