import React from 'react';
import PropTypes from 'prop-types';

class CheckoutForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      completeName: '',
      CPF: '',
      email: '',
      tel: '',
      CEP: '',
      endereco: '',
      complement: '',
      numberOfHouse: '',
      city: '',
      state: '',
      payment: 'billet',
    //   subtitle: '',
    //   title: '',
    //   imagePath: '',
    //   storyline: '',
    //   rating: 0,
    //   genre: 'action',
    };
    this.updateState = this.updateState.bind(this);
    this.submit = this.submit.bind(this);
  }
  updateState(event) {
    const { target } = event;
    this.setState({ [target.name]: target.value });
  }
  submit(event) {
    event.preventDefault();
    // const newMovie = this.state;
    // this.props.addNewMovie(newMovie);
    // this.setState({
    //   subtitle: '',
    //   title: '',
    //   imagePath: '',
    //   storyline: '',
    //   rating: 0,
    //   genre: 'action',
    // });
  }
  render() {
    return (
      <form action="">
        <label htmlFor="completeName-input">
          Nome Completo{' '}
          <input
            id="completeName-input"
            type="text"
            name="completeName"
            value={this.state.completeName}
            onChange={this.updateState}
          />
        </label>
        <br />
        <label htmlFor="CPF-input">
          CPF{' '}
          <input
            id="CPF-input"
            type="text"
            name="CPF"
            onChange={this.updateState}
            value={this.state.CPF}
          />
        </label>
        <br />
        <label htmlFor="email-input">
          Email{' '}
          <input
            id="email-input"
            type="text"
            name="email"
            onChange={this.updateState}
            value={this.state.email}
          />
        </label>
        <br />
        <label htmlFor="tel-input">
          Telefone{' '}
          <input
            id="tel-input"
            type="text"
            name="tel"
            onChange={this.updateState}
            value={this.state.tel}
          />
        </label>
        <br />
        <label htmlFor="CEP-input">
          CEP{' '}
          <input
            id="CEP-input"
            type="text"
            name="CEP"
            onChange={this.updateState}
            value={this.state.CEP}
          />
        </label>
        <br />
        <label htmlFor="endereco-input">
          Endereço{' '}
          <input
            id="endereco-input"
            type="text"
            name="endereco"
            onChange={this.updateState}
            value={this.state.endereco}
          />
        </label>
        <br />
        <label htmlFor="complement-input">
          Complemento{' '}
          <input
            id="complement-input"
            type="text"
            name="complement"
            onChange={this.updateState}
            value={this.state.complement}
          />
        </label>
        <br />
        <label htmlFor="numberOfHouse-input">
          Número{' '}
          <input
            id="numberOfHouse-input"
            type="text"
            name="numberOfHouse"
            onChange={this.updateState}
            value={this.state.numberOfHouse}
          />
        </label>
        <br />
        <label htmlFor="city-input">
          Cidade{' '}
          <input
            id="city-input"
            type="text"
            name="city"
            onChange={this.updateState}
            value={this.state.city}
          />
        </label>
        <br />
        <label htmlFor="state-input">
          Estado{' '}
          <input
            id="state-input"
            type="text"
            name="state"
            onChange={this.updateState}
            value={this.state.state}
          />
        </label>
        <br />
        <label htmlFor="payment-input">
          Forma de Pagamento
          <select
            style={{ marginLeft: 10 }}
            name="payment"
            onChange={this.updateState}
            id="payment-input"
            value={this.state.payment}
          >
            <option value="billet">Boleto</option>
            <option value="visa">Visa</option>
            <option value="masterCard">MasterCard</option>
            <option value="elo">Elo</option>
          </select>
        </label>
        <button onClick={this.submit}>
          Finalizar compra
        </button>
      </form>
    );
  }
}

CheckoutForm.propTypes = { addNewMovie: PropTypes.func.isRequired };

export default CheckoutForm;
