import React, { Component } from 'react';
import * as api from '../services/api'

class FilteredProductsList extends Component {
    constructor() {
        super();
        this.state = {
            categoryList: []
        }
        this.testeCategory = this.testeCategory.bind(this)
    }
    async testeCategory() {
        const testeDeResposta =  await api.getCategories();
        // this.setState({
        //     categoryList: testeDeResposta
        // })
        return testeDeResposta
    }
    render() {
        // this.testeCategory();
        // const {categoryList} = this.state
        return (
            <div>
                {this.testeCategory().map((element) => <p>{element.name}</p>)}
                <ul data-testid="home-initial-message">Digite algum termo de pesquisa ou escolha uma categoria</ul>
            </div>
        )
    }
}

export default FilteredProductsList;
