import React from 'react';
import PropTypes from 'prop-types';

class CheckoutForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      completeName: '',
      CPF: '',
      email: '',
      tel: '',
      CEP: '',
      endereco: '',
      completent: '',
      numberOfHouse: '',
      city: '',
      state: '',
      subtitle: '',
      title: '',
      imagePath: '',
      storyline: '',
      rating: 0,
      genre: 'action',
    };
    this.updateState = this.updateState.bind(this);
    this.submit = this.submit.bind(this);
  }
  updateState(event) {
    const { target } = event;
    this.setState({ [target.name]: target.value });
  }
  submit(event) {
    // event.preventDefault();
    // const newMovie = this.state;
    // this.props.addNewMovie(newMovie);
    // this.setState({
    //   subtitle: '',
    //   title: '',
    //   imagePath: '',
    //   storyline: '',
    //   rating: 0,
    //   genre: 'action',
    // });
  }
  render() {
    return (
      <form action="">
        <label htmlFor="title-input">
          Título{' '}
          <input
            id="title-input"
            type="text"
            name="title"
            value={this.state.title}
            onChange={this.updateState}
          />
        </label>
        <br />
        <label htmlFor="subtitle-input">
          Subtítulo{' '}
          <input
            id="subtitle-input"
            type="text"
            name="subtitle"
            onChange={this.updateState}
            value={this.state.subtitle}
          />
        </label>
        <br />
        <label htmlFor="image-input">
          Imagem{' '}
          <input
            id="image-input"
            type="text"
            name="imagePath"
            onChange={this.updateState}
            value={this.state.imagePath}
          />
        </label>
        <br />
        <label htmlFor="storyline-input">
          Sinopse{' '}
          <textarea
            name="storyline"
            onChange={this.updateState}
            id="storyline-input"
            cols="30"
            rows="10"
            value={this.state.storyline}
          />
        </label>
        <br />
        <label htmlFor="rating-input">
          Avaliação{' '}
          <input
            id="rating-input"
            type="number"
            name="rating"
            onChange={this.updateState}
            value={this.state.rating}
          />
        </label>
        <br />
        <label htmlFor="genre-input">
          Gênero
          <select
            style={{ marginLeft: 10 }}
            name="genre"
            onChange={this.updateState}
            id="genre-input"
            value={this.state.genre}
          >
            <option value="action">Ação</option>
            <option value="comedy">Comédia</option>
            <option value="thriller">Suspense</option>
          </select>
        </label>
        <button onClick={this.submit}>
          Adicionar filme
        </button>
      </form>
    );
  }
}

CheckoutForm.propTypes = { addNewMovie: PropTypes.func.isRequired };

export default CheckoutForm;
