import React, { Component } from 'react';
import * as api from '../services/api';
import SearchBar from '../components/SearchBar';
import FilteredProductsList from '../components/FilteredProductsList';

class ProductsList extends Component {
  constructor() {
    super();
    this.state = {
      productsToRender: [],
    };
    this.fetchByQuery = this.fetchByQuery.bind(this);
  }

  async fetchByQuery(query) {
    const fetchResult = api.getProductsFromCategoryAndQuery(query);
    this.setState({
      productsToRender: fetchResult,
    });
  }

  render() {
    const { productsToRender } = this.state;
    return (
      <div>
        <SearchBar onSearch={ this.fetchByQuery } />
        <FilteredProductsList allProducts={ productsToRender } />
      </div>
    );
  }
}

export default ProductsList;
