import React, { Component } from 'react';
import IndividualCard from './IndividualCard';
import * as cartFunctions from '../services/cartFunctions';

class ProductsInCheckoutList extends Component {
    constructor() {
        super();
        this.state = {
            shoppingCartItems: cartFunctions.recoveryProductsFromLocalStorage(),
            totalPrice: cartFunctions.recoveryProductsFromLocalStorage().reduce((totalPrice, product) => {
                return (
                    (totalPrice + product.price)
                )
            }, 0)
        };
    }
    render() {
        const { shoppingCartItems } = this.state;
        return(
            <section>
                <h3>Revise seus produtos</h3>
                <div>
                {shoppingCartItems.map((product) => (
<IndividualCard
            key={ product.id }
            id={ product.id }
            title={ product.title }
            price={ product.price }
            image={ product.thumbnail }
          />
        ))}
                </div>
            </section>
        )
    }
}

export default ProductsInCheckoutList;
